import React, { useState } from "react";
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  Stack,
  Heading,
  Text,
  Link,
  useToast,
  Icon,
  Flex,
} from "@chakra-ui/react";
import { FaUserLock } from "react-icons/fa";

const LoginPage = ({ onSwitch }) => {
  const [form, setForm] = useState({ email: "", password: "" });
  const toast = useToast();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleLogin = () => {
    if (!form.email || !form.password) {
      return toast({
        title: "Missing Fields",
        description: "Email and Password are required.",
        status: "warning",
        duration: 3000,
        isClosable: true,
      });
    }

    // Replace with your login logic (API call)
    console.log("Logging in:", form);
  };

  return (
    <Flex
      minH="100vh"
      align="center"
      justify="center"
      bgGradient="linear(to-br, teal.50, white)"
      px={4}
    >
      <Box
        maxW="md"
        w="100%"
        bg="white"
        p={8}
        rounded="lg"
        shadow="lg"
        border="1px solid"
        borderColor="gray.100"
      >
        <Stack spacing={4} align="center" mb={4}>
          <Icon as={FaUserLock} boxSize={8} color="teal.500" />
          <Heading size="md" color="teal.600">
            Login to TechinvoPeople
          </Heading>
          <Text fontSize="sm" color="gray.500" textAlign="center">
            Welcome back! Please enter your credentials to continue.
          </Text>
        </Stack>

        <Stack spacing={4}>
          <FormControl>
            <FormLabel fontSize="sm">Email</FormLabel>
            <Input
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="you@example.com"
            />
          </FormControl>

          <FormControl>
            <FormLabel fontSize="sm">Password</FormLabel>
            <Input
              type="password"
              name="password"
              value={form.password}
              onChange={handleChange}
              placeholder="••••••••"
            />
          </FormControl>

          <Button colorScheme="teal" onClick={handleLogin}>
            Login
          </Button>
        </Stack>

        <Text fontSize="sm" mt={6} textAlign="center">
          Don’t have an account?{" "}
          <Link color="teal.500" fontWeight="medium" onClick={onSwitch}>
            Sign up
          </Link>
        </Text>
      </Box>
    </Flex>
  );
};

export default LoginPage;
